CREATE TABLE seo_kaigo_prefecture_texts (
    `id` INT(11) unsigned NOT NULL AUTO_INCREMENT,
    `prefecture_code` VARCHAR(4) NOT NULL COMMENT '都道府県code',
    `high_income` TEXT COMMENT '高収入',
    `popular_keyword` TEXT COMMENT '人気キーワード',
    `hellowork_area` TEXT COMMENT 'ハロワエリア',
    `del_flg` TINYINT(1) NOT NULL DEFAULT '0' COMMENT '削除フラグ',
    `created` DATETIME NOT NULL COMMENT '作成時刻',
    `modified` DATETIME NOT NULL COMMENT '更新時刻',
    PRIMARY KEY (`id`)
);
